mapper.select:
SELECT tid, name, table_weight AS tableWeight, scan_freq_per_min AS scanFreqPerMin
FROM sys_table_info
WHERE tid = #{tid}

mapper.update:
UPDATE sys_table_info
SET table_weight = #{weight}
WHERE tid = #{tid}

